var countdown_timer = null;
function countdownTimer(intDiff){
	if(countdown_timer != null) {
		window.clearInterval(countdown_timer);
	}
	countdown_timer =  window.setInterval(function(){
    var day=0,
        hour=0,
        minute=0,
        second=0;//时间默认值       
    if(intDiff > 0){
        day = Math.floor(intDiff / (60 * 60 * 24));
        hour = Math.floor(intDiff / (60 * 60)) - (day * 24);
        minute = Math.floor(intDiff / 60) - (day * 24 * 60) - (hour * 60);
        second = Math.floor(intDiff) - (day * 24 * 60 * 60) - (hour * 60 * 60) - (minute * 60);
    }
    if(day == 0 && hour == 0 && minute == 0 && second == 0){
        $("span[id = 'time']").html("已封盘");
        art.dialog.close();
        //entertainedShwo();//封盘效果显示4
    } else {
    	if (minute <= 9) minute = '0' + minute;
        if (second <= 9) second = '0' + second;
        $("span[id = 'time']").html(""+maxText+" : " +day+"天"+hour+'时'+minute+'分'+second+'秒');
    }
    intDiff--;
    }, 1000);
} 